import React, { useEffect, useState } from 'react';

const MailList = () => {
    const [buttonHeight, setButtonHeight] = useState(30); // Altezza iniziale del button

    const increaseButtonHeight = () => {
      setButtonHeight((prevHeight) => prevHeight + 10); // Aumenta l'altezza del button di 10 pixel
    };

    return (
        <div class="list-group" data-bs-theme="dark" style={{borderRadius: '0px'}}>
            <button type="button" class="list-group-item list-group-item-action" style={{backgroundColor: '#05061a'}} onClick={increaseButtonHeight}>The current button</button>
            <button type="button" class="list-group-item list-group-item-action" style={{backgroundColor: '#05061a'}} onClick={increaseButtonHeight}>A second button item</button>
            <button type="button" class="list-group-item list-group-item-action" style={{backgroundColor: '#05061a'}} onClick={increaseButtonHeight}>A third button item</button>
            <button type="button" class="list-group-item list-group-item-action" style={{backgroundColor: '#05061a'}} onClick={increaseButtonHeight}>A fourth button item</button>
            <button type="button" class="list-group-item list-group-item-action" style={{backgroundColor: '#05061a'}} onClick={increaseButtonHeight}>A disabled button item</button>
        </div>
    );
}

export default MailList;
